mapper.insert:
INSERT INTO users(
    user_uuid, email, token_id)
    VALUES(
       #{userUuid}, #{email}, #{tokenId}
    )

mapper.update:
UPDATE users SET
name = #{name},
image_url = #{imageUrl}
WHERE email = #{email}

UPDATE users SET
token_id = #{tokenId},
image_url = #{imageUrl}
WHERE email = #{email}
